import React, { Component } from "react";
import axios from 'axios';

class Statistics extends Component {
    constructor(props){
        super(props)
        this.state  = {
            statisticsData : {"downloads":1000,"likes":200,"reviews":17,"verstion":6}
        }
    }

    fetchStatisticsData() {
        // Where we're fetching data from
        axios.get(`https://hulugram-beta.firebaseio.com/appstat.json`)
          // We get the API response and receive data in JSON format...
          .then(response => {
            console.log(response)
            this.setState({statisticsData: response.data})
          })
          // Catch any errors we hit and update the app
          .catch(error => {
                  console.log(error)
          });
        }
        
     componentDidMount() { 
      this.fetchStatisticsData();
      console.log(this.state.statisticsData);
     }
  render() {
    
    let {downloads, likes, reviews,verstion}= this.state.statisticsData;

    return (
      <React.Fragment>
      <div>
      <ul className="stat">
      <li>
      <h1>{downloads}</h1>
      <p>Downloads</p>
      </li>

      <li>
      <h1>{likes}</h1>
      <p>Likes</p>
      </li>

      <li>
      <h1>{reviews}</h1>
      <p>Reviews</p>
      </li>

      <li>
      <h1>{verstion}</h1>
      <p>Verstion</p>
      </li>

      </ul>
      </div>
      </React.Fragment>
    ); 
  }
}




// {"downloads":1000,"likes":200,"reviews":17,"verstion":6}

export default Statistics;
