import React from 'react';
import axios from 'axios';


class DownloadHulugram extends React.Component {
	
	constructor(props) {
		super(props);
	}
	
	downloadHulugramApp() {
		axios.get(`http://localhost:8080/employees/download`)
			.then(response => {
				response.blob().then(blob => {
					let url = window.URL.createObjectURL(blob);
					let a = document.createElement('a');
					a.href = url;
					a.download = 'hulugram.json';
					a.click();  
				});
				//window.location.href = response.url;
		});
	}
	
	render() {
		return (
			<div id="container">
				<h1>Download File using React App</h1>
				<h3>Download Employee Data using Button</h3>
				<button onClick={this.downloadHulugramApp}>Download</button>
				<p/>
			</div>
		)
	}

}

export default DownloadHulugram;
