import React, { Component } from "react";
import axios from 'axios';

class Statistics extends Component {
    constructor(props){
        super(props)
        this.state  = {
          isLoading: true,
          statisticsData : {},
          error: null
            
        }
    }

    fetchStatisticsData() {
        // Where we're fetching data from
        axios.get(`https://hulugram-beta.firebaseio.com/appstat.json`)
          // We get the API response and receive data in JSON format...
          .then(response => {
            console.log(response)
            this.setState({
              statisticsData: response.data,
              isLoading: false
            });
          })
          // Catch any errors we hit and update the app
          .catch(error => this.setState({ error, isLoading: false })); 
        }

     componentDidMount() { 
      this.fetchStatisticsData();
      console.log(this.state.statisticsData);
     }

  render() {
    const { isLoading, statisticsData, error } = this.state;
    if(error )
    return (
      <React.Fragment>
      <h1 className="loading">{error.message}</h1>
      </React.Fragment>
      )
  else
    return (
      <React.Fragment>
  

      {!isLoading && !error? (
            <div>
      <ul className="stat">
      <li>
      <h1>{statisticsData.downloads}</h1>
      <p>Downloads</p>
      </li>

      <li>
      <h1>{statisticsData.likes}</h1>
      <p>Likes</p>
      </li>

      <li>
      <h1>{statisticsData.reviews}</h1>
      <p>Reviews</p>
      </li>

      <li>
      <h1>{statisticsData.verstion}</h1>
      <p>Verstion</p>
      </li>

      </ul>
      </div>
      // If there is a delay in data, let's let the user know it's loading
      ) : (
        <h1 className="loading">Loading...</h1>
      )}



            </React.Fragment>
    ); 
  }
}




// {"downloads":1000,"likes":200,"reviews":17,"verstion":6}

export default Statistics;
